/* proxy.js simulates connections to the router. this is useful for
 * testing, accessing resources limited to a local network, and
 * bypassing certain forms of network monitoring or censorship.
*/

var R = require('rooter');
var rooter = R.rooter;
var Client = R.Client;

var ProxyClient = function() {
    Client.call(this);
};
ProxyClient.prototype = new Client();

var proxy = {};
proxy.create = function() {
    var pc = new ProxyClient();
    rooter.trigger("connect", pc);
    return pc;
};

module.exports = proxy;
