var Triggerable = require('./triggerable.js');

var Client = function() {
    Triggerable.call(this);
    this.registerEvent("disconnect");
};
Client.prototype = new Triggerable;

Client.prototype.disconnect = function() {
    this.trigger("disconnect", this);
};
Client.prototype.toString = function() {
    // This will improve once clients have information contained within.
    return '[client]';
};

module.exports = Client;
